import "./header.css"
import RegisterCandidato from "../../pages/AuthCandidato/Register";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";


function Header(){
    const navigate = useNavigate();
    const [logado, setLogado] = useState(false);
    const [tipoUsuario, setTipoUsuario] = useState("");

    useEffect(()=>{
        const token = localStorage.getItem('token');
        const tipoUsuario = localStorage.getItem("tipoUsuario")
        if(token && tipoUsuario){
            setLogado(true)
        }else{
            setLogado(false);
        }
    }, [])

    const handleLogout = () =>{
            localStorage.removeItem('token');
            localStorage.removeItem('tipoUsuario')
            setLogado(false);
            navigate("/");
    }

    return(
        <>
        <header>
            <div>
                <h2>CONNECT VAGAS</h2>
            </div>
            
            <nav>
                <ul>   
                    {logado ? (
                    <>
                        
                     {/* <li><a>VAGAS</a></li> */}
                     <li><button className="btnCandidato">Área do Candidato</button></li>
                     <li><button className="btnSair" onClick={handleLogout}>Sair</button></li>
                     </>
                    ) : (
                    <>
                    {/* <li><a>VAGAS</a></li> */}
                    <li><a href="/register/candidato">CANDIDATO</a></li>
                    <li><a>MINHA EMPRESA</a></li>
                    </>
                    )}
                </ul>
            </nav>
        </header>
        </>
    )
}
export default Header;
