import { useEffect, useState } from 'react';
import {listarVagas} from '../services/api'
import './home.css'

function Home(){

    const [vagas, setVagas] = useState([]);

    useEffect(()=>{
        const loadVagas = async ()=>{
            const data = await listarVagas();
            if(data) setVagas(data);
        }

        loadVagas();
    }, []);

    return(
        <>
        <div className='warning-home'>
            <p>A ConnectVagas é gratuita para candidatos e nunca se deve fazer pagamentos a empresas ou pessoas.
                Denuncie qualquer tipo de fraude.
            </p>
        </div>

        <div className='div-inputs-query'>
            <div className='inputs-query'>
            <input className='input-job' placeholder='Encontre a vaga que você deseja.'></input>
            <input className='input-local' placeholder='Localização'></input>
            <button>Buscar</button>
            </div>
        </div>

        <div className='div-divisor'>
            <hr></hr>
        </div>

        <section className='jobs-available'>
            <span>Todas as vagas disponíveis.</span>

            <ul>
                {vagas.map((vaga)=>(
                    <li key={vaga._id}>
                        <h2>{vaga.cargo}</h2>
                        <span>{vaga.id_empresa}</span>
                        <p>{vaga.descricao}</p>
                        <button>Candidatar-se</button>
                    </li>
                ))}
            </ul>
        </section>

        </>
    )
}

export default Home;
