import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Document, Page } from 'react-pdf';
import { pdfjs } from 'react-pdf';

// Defina o caminho do worker (ajuste conforme necessário)
pdfjs.GlobalWorkerOptions.workerSrc = `https://unpkg.com/pdfjs-dist@2.10.377/es5/build/pdf.worker.min.js`;



function CurriculoViewer() {
  const { filename } = useParams();
  const [pdfUrl, setPdfUrl] = useState('');
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  // Atualiza a URL do PDF conforme o parâmetro 'filename'
  useEffect(() => {
    setPdfUrl(`/uploads/${filename}`);
  }, [filename]);

  // Função chamada ao carregar o documento PDF
  const onLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  return (
    <div>
      <h1>Visualizar Currículo</h1>
      
      {/* Exibindo o PDF */}
      <Document file={pdfUrl} onLoadSuccess={onLoadSuccess}>
        <Page pageNumber={pageNumber} />
      </Document>
      
      {/* Navegação entre as páginas do PDF */}
      <div>
        <p>
          Página {pageNumber} de {numPages}
        </p>
        <button 
          disabled={pageNumber <= 1} 
          onClick={() => setPageNumber(pageNumber - 1)}
        >
          Anterior
        </button>
        <button 
          disabled={pageNumber >= numPages} 
          onClick={() => setPageNumber(pageNumber + 1)}
        >
          Próxima
        </button>
      </div>
    </div>
  );
}

export default CurriculoViewer;
