import api from "../../services/api";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function ProfileCandidato() {
  const [perfil, setPerfil] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const InfosProfile = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        console.log("Nenhum token encontrado. Redirecionando para login...");
        navigate("/login/candidato"); 
        return;
      }

      try {
        const response = await api.get("/candidato/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setPerfil(response.data);
      } catch (error) {
        console.error("Erro ao buscar perfil:", error);
        alert("Erro ao carregar perfil. Faça login novamente.");
        localStorage.removeItem("token"); 
        navigate("/login/candidato");
      }
    };

    InfosProfile();
  }, [navigate]);

  if (!perfil) {
    return <div>Carregando...</div>;
  }

  return (
    <div className="profile-container">
      <h2>Perfil do Candidato</h2>
      <p><strong>Nome:</strong> {perfil.nome}</p>
      <p><strong>Email:</strong> {perfil.email}</p>
      <p><strong>Telefone:</strong> {perfil.telefone || "Não informado"}</p>
      <p><strong>Área de Interesse:</strong> {perfil.area_interesse || "Não informada"}</p>
    </div>
  );
}

export default ProfileCandidato;
